import React, { useState } from 'react'
import { useParams } from 'react-router-dom';
import Checkbox from '@mui/material/Checkbox';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import { updatePost } from '../api';

const Editpost = ({ posts, token, navigate, fetchPosts }) => {
   const [newTitle, setNewTitle] = useState('');
   const { id } = useParams();

   const [currentPost] = posts.filter(post => post._id === id);
   const {title, description, location, price, willDeliver} = currentPost;
   const [newDescription, setNewDescription] = useState(description);
   const [newLocation, setNewLocation] = useState(location);
   const [newPrice, setNewPrice] = useState(price);
   const [newWillDeliver, setNewWillDeliver] = useState(willDeliver);

async function editPost() {
    try {
        const updatedPost = {
          token: token,
          title: newTitle,
          description: newDescription,
          location: newLocation,
          price: newPrice,
          willDeliver: newWillDeliver,
          _id: id
        }
        await updatePost(updatedPost)
        navigate('/posts');
    } catch (error) {
        console.log("error")
    }
  }


  return (
    <Box  
        component="form"
        sx={{
          '& .MuiTextField-root': { m: 1, width: '25ch' },
        }}
        noValidate
        autoComplete="off"
        onSubmit={(event) => {
            event.preventDefault();
            editPost();
        }}
    >
    <h1>Edit Post</h1>






    <input
        type='text'
        placeholder={title}
        onChange={(event) => setNewTitle(event.target.value)}
    >
    </input>
    <input
        type='text'
        placeholder={description}
        onChange={(event) => setNewDescription(event.target.value)}
    />
    <input
        type='text'
        placeholder={location}
        onChange={(event) => setNewLocation(event.target.value)}
    />
    <input
        type='text'
        placeholder={price}
        onChange={(event) => setNewPrice(event.target.value)}
    />
    <input
        type='text'
        placeholder={willDeliver}
        onChange={(event) => setNewWillDeliver(event.target.value)}
    />
    <Button  
        type="submit"
        variant="outlined"
        style={{width:"30rem"}}>
    Edit Post</Button> 
    </Box>
  )
}

export default Editpost;

