import React, { Fragment, useState } from "react";
import { Paper, Button } from "@mui/material";
import { Link, Router } from "react-router-dom";
import Createpost from "./CreatePost";
import SinglePost from "./SinglePost";
import { deletePost } from '../api';
import { useNavigate } from 'react-router-dom';
import "./posts.css"


const postMatches = (post, searchTerm, setPosts) => {
    const searchTermLower = searchTerm.toLowerCase();
    const {
      description,
      location,
      title,
      author: { username },
    } = post;

    const toMatch = [description, location, title, username];

    for (let i = 0; i < toMatch.length; i++) {
      const field = toMatch[i];
      if (field.toLowerCase().includes(searchTermLower)) {
        return true;
      }
    }
  };

const Post = ({ posts, token, fetchPosts }) => {

    const navigate = useNavigate();
    const [searchTerm, setSearchTerm] = useState("");

    const postsToDisplay = posts.filter((post) => postMatches(post, searchTerm));

    return (
        <div>
            <Button
                className="create-post-button"
                variant="outlined"
            >
            <Link className="create-post-link" to="/createpost" element={<Createpost />}>Create Post</Link>
            </Button>
            <input 
              type="text" 
              placeholder="Search Posts"
              value={searchTerm}
              onChange={(event) => {
                setSearchTerm(event.target.value)
              console.log(searchTerm)}}
            ></input>
            <Button
                variant="outlined"
            >Search</Button>
        {
            posts.map((post) => {
                const { description, location, price, title, _id, isAuthor } = post
                return (
                    <Paper
                    style={{margin:"1rem"}}
                    elevation={5}
                    key={_id}
                    >
                    <Fragment>
                    <div>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Price: {price}</p>
                        <p>Location: {location}</p>
                        {
                            isAuthor ? (
                                <Button
                                variant="outlined"
                                >
                                    <Link 
                                    className="edit-link"
                                    to={`/posts/edit-post/${_id}`}
                                    >Edit</Link>
                                </Button>
                            ) : (
                                <Button
                                variant="outlined"
                                >
                                <Link className="view-link" to={`/posts/${_id}`}>View</Link>
                                </Button>
                            )
                        }
                         {
                            isAuthor ? (
                                <Button
                                    color="error"
                                    variant="outlined"
                                    onClick={() => {
                                        deletePost(_id, token)
                                        fetchPosts()
                                    }}
                                >Delete</Button>
                            ) : (
                                <p></p>
                            )
                        }
                    </div>
                    </Fragment>
                    </Paper>
                )
            })
        }
        </div>
    )
}

export default Post

{postsToDisplay.length ? (
  postsToDisplay.map((post) => (
    <Paper
      style={{margin:"1rem"}}
                    elevation={5}
                    key={_id}

    >
    <div
      className="post-info"
      key={post._id}
      style={{ border: "2px solid black" }}
    >
      <div id="title-button">
        <span className="post-title">{post.title}</span>
        <button onClick={() => history.push(`/posts/${post._id}`)}>
          Tell me more!
        </button>
      </div>
      <div id="seller">Seller: {post.author.username}</div>
      <div className="location">Location: {post.location}</div>
      <div className="description">Description: {post.description}</div>

      {post.author.username === userData.username ? (
        <button onClick={() => handleSubmit(post._id)}>
          Delete Post
        </button>
      ) : null}
    </div>
    </Paper>
  ))
) : (
  <div>
    <h1>There are no matching posts...</h1>
  </div>
)}
