import React, { useState } from 'react'
import { Route } from 'react-router-dom';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import "./createpost.css";
import { createPost, getUserDetails } from '../api';
import Checkbox from '@mui/material/Checkbox';


const Createpost = () => {

  const [token, setToken] = useState('');
  console.log(token)

  se
  const [title, setTitle] = useState('');
  console.log(title)
  const [description, setDescription] = useState('');
  console.log(description)
  const [price, setPrice] = useState('');
  console.log(price)
  const [location, setLocation] = useState('');
  console.log(location)
  const [willDeliver, setWillDeliver] = useState(false);
  console.log(willDeliver)

  const handleCreate = async(token) => {
    const results = await createPost(token, title, description, price, location, willDeliver);
    console.log(results)
    // if(results.success) {
    //     console.log("this was a success")
    // } else {
    //     console.log("this is an error message")
    // }
  }

  return (
    <Box  
    component="form"
    sx={{
      '& .MuiTextField-root': { m: 1, width: '25ch' },
    }}
    noValidate
    autoComplete="off"
    onSubmit={(event) => {
        event.preventDefault();
        handleCreate();
    }}
    >
    <div className='create-post-form'>
        <h1>Create Post</h1>
        <TextField
            style={{width:"30rem"}}
            required
            label="Title"
            onChange={(event) => setTitle(event.target.value)}
        />
        <TextField
            style={{width:"30rem"}}
            required
            label="Description"
            onChange={(event) => setDescription(event.target.value)}
        />
        <TextField
            style={{width:"30rem"}}
            required
            label="Price"
            onChange={(event) => setPrice(event.target.value)}
        />
        <TextField
            style={{width:"30rem"}}
            required
            label="Location"
            onChange={(event) => setLocation(event.target.value)}
        />
        <p id='will-you-deliver-message'>Will you deliver?</p>
        <Checkbox
        onClick={(event) => {
          if(willDeliver === true) {
            setWillDeliver(false)
          } else {
            setWillDeliver(true)}
          }
        }
        />
        <Button
        type="submit"
        variant="outlined"
        style={{width:"30rem"}}
        >Create</Button>
    </div>
    </Box>
  )
}

export default Createpost;

