import React from 'react';
import { useParams } from 'react-router-dom';
import { Paper } from '@mui/material';
import { createMessage } from '../api';

const SendMessage = ({ id, token }) => {
  const [message, setMessage] = useState({content: ''});
  // we need 3 things to make this request
    // Post-id, token, message object containing the content of the message

  async function addMessage() {
    await createMessage({post, message, token})
  }

  return (
    <form onSubmit={ (ev)=> {
      ev.preventDefault();
      addMessage();
    }}>
      <input
        type='text'
        placeholder='Enter Message'
        onChange={ (ev) => setMessage({content: ev.target.value}) }
      />
      <button type='submit'>Send Message</button>
    </form>
  )
}


const SinglePost = ({posts}) => {
  const { id } = useParams();

  const [currentPost] = posts.filter(post => post._id === id);
  const {title, description, location, price, willDeliver} = currentPost;

  return (
    <Paper
    style={{margin:"1rem"}}
    elevation= {5}
    >
    <div>
      <h3>{title}</h3>
      <p>Description: {description}</p>
      <p>Price: {price}</p>
      <p>Location: {location}</p>
      <p>Will Deliver: {willDeliver}</p>
    </div>
    </Paper>
  )
}

export default SinglePost;
