import React, { Fragment } from "react";
import { Paper } from "@mui/material";
import { Link } from "react-router-dom";
import Createpost from "./CreatePost";
import { display } from "@mui/system";
import { deletePost } from '../api';
import "./posts.css"


const Post = ({ posts, token }) => {

    const handleDelete = async() => {
        try{
          const results = await deletePost(token, _id);
        } catch (error) {
          console.log("this is an error message")
        }
      }

    return (
        <div>
            <Link className="create-post-link" to="/createpost" element={<Createpost />}>Create Post</Link>
        {
            posts.map((post) => {
                const { description, location, price, title, _id, isAuthor } = post
                return (
                    <Paper
                    style={{margin:"1rem"}}
                    elevation= {5}
                    key={_id}
                    >
                    <Fragment>
                    <div>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Price: {price}</p>
                        <p>Location: {location}</p>
                        {
                            isAuthor ? (
                                <Link to={`/posts/edit-post/${_id}`} >You are the author</Link>
                            ) : (
                                <Link to={`/posts/${_id}`}>View</Link>
                            )
                        }
                         {
                            isAuthor ? (
                                <button
                                    onClick={(token, _id) => { 
                                        handleDelete()
                                        console.log("delete")
                                        deletePost(_id, token)
                                    // console.log(token)
                                    }}
                                >Delete</button>
                            ) : (
                                <button>No</button>
                            )
                        }
                    </div>
                    </Fragment>
                    </Paper>
                )
            })
        }
        </div>
    )
}

export default Post


