import React, { Fragment } from "react";
import { Paper } from "@mui/material";
import { Link } from "react-router-dom";
import Createpost from "./Createpost";
import { display } from "@mui/system";
import "./posts.css"


const Post = ({ posts }) => {
    return (
        <div>
            <Link className="create-post-link" to="/createpost" element={<Createpost />}>Create Post</Link>
        {
            posts.map((post) => {
                const { description, location, price, title, _id } = post
                return (
                    <Paper
                    style={{margin:"1rem"}}
                    elevation= {5}
                    key={_id}
                    >
                    <Fragment>
                    <div>
                        <h3>{title}</h3>
                        <p>Description: {description}</p>
                        <p>Price: {price}</p>
                        <p>Location: {location}</p>
                        {
                            isAuthor ? (
                                <button>You are the author</button>
              ) : (
                <Link to={`/posts/${_id}`}>View</Link>
              )
            }
                    </div>
                    </Fragment>
                    </Paper>
                )
            })
        }
        </div>
    )
}

export default Post
